import React from 'react';
import Footer from '../../Footer/Footer';
import Header from '../Header';
 import './Gallery.css'

 import { AiFillHeart , AiFillCamera  , AiOutlineRise } from "react-icons/ai";

function Gallery() {
    const handleClick = () => {
        setSelected('تازه ها');  // وقتی روی "تازه ها" کلیک شد، آن را به عنوان انتخاب شده ذخیره می‌کنیم
      };
  return (
    <>
      <Header />
      
      <div className="box-main">
        <div className="box-flex">
        <div className="text-icon" style={{marginRight:'30px'}}>
        <AiFillHeart/>
            <span> محبوب </span>
        </div>

        <div className="text-icon"   onClick={handleClick}>
        <AiFillCamera/>
            <span> تازه ها </span>
        </div>

        <div className="text-icon">
        <AiOutlineRise />
            <span>    درحال پیشرفت </span>
        </div>
        </div>
      </div>

      <Footer />
    </>
  );
}

export default Gallery;
