// import React from 'react';
// import './Gallery.css';  
// import Footer from '../../Footer/Footer'
// import Header from '../Header'
// function Gallery() {
//     return (
//         <>
//         <Header />
//         <Footer/>          
//         </>
//     );
// }

// export default Gallery;



const App = () => {
  const [selected, setSelected] = useState(""); // پیگیری گزینه انتخاب شده
  const [anchorEl, setAnchorEl] = useState(null); // کنترل باز یا بسته بودن منو

  const handleClick = (option) => {
    setSelected(option);
  };

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const menuItems = [
    "طبیعت",
    "چشم انداز ها و مناظر",
    "حیات وحش",
    "معماری",
    "هوایی",
    "اشیا و طبیعت بی جان",
    "نجومی",
    "شب",
    "سیاه سفید",
    "پرتره",
    "ماکرو",
    "خبری و ورزشی و مستند",
  ];

  return (
    <Box className="boxContainer">
      {/* گزینه‌های محبوب */}
      <Box className="options">
        <Box
          className={`optionItem ${selected === "محبوب ها" ? "selected" : ""}`}
          onClick={() => handleClick("محبوب ها")}
        >
          <FavoriteIcon />
          <Typography>محبوب ها</Typography>
        </Box>
        <Box
          className={`optionItem ${selected === "تازه ها" ? "selected" : ""}`}
          onClick={() => handleClick("تازه ها")}
        >
          <ImageIcon />
          <Typography>تازه ها</Typography>
        </Box>
        <Box
          className={`optionItem ${
            selected === "در حال پیشرفت" ? "selected" : ""
          }`}
          onClick={() => handleClick("در حال پیشرفت")}
        >
          <ArrowUpwardIcon />
          <Typography>در حال پیشرفت</Typography>
        </Box>
      </Box>

      {/* اینپوت دسته‌بندی‌ها */}
      <Box className="categoryInput">
        <InputBase placeholder="دسته بندی ها" />
        <IconButton onClick={handleMenuOpen}>
          <ArrowDropDownIcon />
        </IconButton>
        <Menu
          anchorEl={anchorEl}
          open={Boolean(anchorEl)}
          onClose={handleMenuClose}
        >
          {menuItems.map((item, index) => (
            <MenuItem key={index} onClick={handleMenuClose}>
              {item}
            </MenuItem>
          ))}
        </Menu>
      </Box>
    </Box>
  );
};

export default App;
