import { BrowserRouter, Routes, Route } from 'react-router-dom';

function App() {
  return (
    <BrowserRouter>
      <Routes>
        {/* مسیر گالری فقط گالری را نمایش می‌دهد */}
        <Route 
          path="/Gallery" 
          element={<Gallery />} 
        />
        {/* سایر مسیرها شامل هدر، فوتر و محتوا */}
       
         
      </Routes>
    </BrowserRouter>
  );
}

export default App;
