import React, { useState } from 'react';
import Footer from '../../Footer/Footer';
import Header from '../Header';
import './Gallery.css';

import { AiFillHeart, AiFillCamera, AiOutlineRise } from 'react-icons/ai';

function Gallery() {
  // State برای ذخیره کردن انتخاب شده
  const [selected, setSelected] = useState(null);

  // Event handler برای مدیریت کلیک‌ها
  const handleClick = (section) => {
    setSelected(section);  // وقتی روی یک بخش کلیک شد، آن بخش به عنوان انتخاب شده ذخیره می‌شود
  };

  return (
    <>
      <Header />
      
      <div className="box-main">
        <div className="box-flex">
          <div 
            className="text-icon"
            style={{
              marginRight: '30px',
              border: selected === 'محبوب' ? '4px solid green' : 'none', // تغییر استایل زمانی که انتخاب شده
            }}
            onClick={() => handleClick('محبوب')} // وقتی کلیک شد، 'محبوب' انتخاب می‌شود
          >
            <AiFillHeart />
            <span>محبوب</span>
          </div>

          <div 
            className="text-icon"
            style={{
              border: selected === 'تازه ها' ? '4px solid green' : 'none', // تغییر استایل زمانی که انتخاب شده
            }}
            onClick={() => handleClick('تازه ها')} // وقتی کلیک شد، 'تازه ها' انتخاب می‌شود
          >
            <AiFillCamera />
            <span>تازه ها</span>
          </div>

          <div 
            className="text-icon"
            style={{
              border: selected === 'درحال پیشرفت' ? '4px solid green' : 'none', // تغییر استایل زمانی که انتخاب شده
            }}
            onClick={() => handleClick('درحال پیشرفت')} // وقتی کلیک شد، 'درحال پیشرفت' انتخاب می‌شود
          >
            <AiOutlineRise />
            <span>درحال پیشرفت</span>
          </div>
        </div>
      </div>

      <Footer />
    </>
  );
}

export default Gallery;
